import Movies from './movies';
import axios from 'axios';


class Read extends React.Component{
constructor(){
    super();
    this.ReloadDataMethod = this.ReloadDataMethod.bind(this);
}
    state = {
        movies: []
    };

    componentDidMount() {
        axios.get('http://localhost:4000/api/movies')
        .then((response)=>{
            this.setState({movies: response.data.movies})
        })
        .catch((error)=>{
            console.log(error);
        });
    }

    ReloadDataMethod(){
        axios.get('http://localhost:4000/api/movies')
        .then((response)=>{
            this.setState({movies: response.data.movies})
        })
        .catch((error)=>{
            console.log(error);
        });
    }
    render(){
        return(
            <div>
                <h1>Hello from Read Component</h1>
                <Movies myMovies={this.state.movies} ReloadDataMethod={this.ReloadDataMethod} ></Movies>
            </div>
        );
    }
}
export default Read;
