import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link } from 'react-router-dom';


class Book extends React.Component {

  constructor() {
    super();
    //this.DeleteBookDetails = this.DeleteBookDetails.bind(this);
  }

  DeleteMovie(e) {
    console.log("Delete Clicked");
    axios.delete("http://localhost:4000/api/bookDetails/" + this.props.bookDetails._id)
      .then(() => {
        this.props.ReloadDataMethod();
      })
      .catch();

  }

  render() {
    return (
      <div>
        {/* <h4>{this.props.movie.Title}</h4>
                <p>{this.props.movie.Year}</p>
                <img src={this.props.movie.Poster}></img> */}


        <Card border="primary" style={{ width: '28rem' }}>
          <Card.Header>{this.props.bookDetails.Title}</Card.Header>
          <Card.Body>
            <blockquote className="blockquote mb-0">
              <img src={this.props.bookDetails.Cover}></img>
              <footer>
                {this.props.bookDetails.Year}
              </footer>
            </blockquote>
          </Card.Body>
          <Button variant="danger" onClick={this.DeleteBookDetails}>Delete</Button>
          <Link to={"/change/" + this.props.bookDetails._id} className="btn btn-primary">Change</Link>
        </Card>
      </div>
    )
  }
}
export default Book;
